/**
 * v-magnetic directive
 * Subtly attracts an element toward the cursor when hovering nearby.
 * Desktop only, respects prefers-reduced-motion.
 * Registered universally so SSR doesn't fail, but logic only runs client-side.
 *
 * Usage: <button v-magnetic>Click me</button>
 *        <button v-magnetic="{ strength: 0.4, radius: 120 }">Custom</button>
 */
export default defineNuxtPlugin((nuxtApp) => {
  nuxtApp.vueApp.directive("magnetic", {
    getSSRProps() {
      return {};
    },

    mounted(el: HTMLElement, binding) {
      const prefersReduced = window.matchMedia(
        "(prefers-reduced-motion: reduce)"
      ).matches;

      const isMobile =
        "ontouchstart" in window ||
        navigator.maxTouchPoints > 0 ||
        window.matchMedia("(max-width: 1024px)").matches;

      if (prefersReduced || isMobile) return;

      const opts = binding.value ?? {};
      const strength = opts.strength ?? 0.3;
      const radius = opts.radius ?? 100;

      let raf: number | null = null;
      let currentX = 0;
      let currentY = 0;
      let targetX = 0;
      let targetY = 0;
      let isActive = false;

      const lerp = (a: number, b: number, t: number) => a + (b - a) * t;

      const animate = () => {
        currentX = lerp(currentX, targetX, 0.15);
        currentY = lerp(currentY, targetY, 0.15);

        if (
          Math.abs(currentX - targetX) < 0.1 &&
          Math.abs(currentY - targetY) < 0.1
        ) {
          currentX = targetX;
          currentY = targetY;
        }

        el.style.transform = `translate(${currentX}px, ${currentY}px)`;

        if (isActive || Math.abs(currentX) > 0.1 || Math.abs(currentY) > 0.1) {
          raf = requestAnimationFrame(animate);
        } else {
          el.style.transform = "";
          raf = null;
        }
      };

      const onMouseMove = (e: MouseEvent) => {
        const rect = el.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;
        const distX = e.clientX - centerX;
        const distY = e.clientY - centerY;
        const dist = Math.sqrt(distX * distX + distY * distY);

        if (dist < radius) {
          isActive = true;
          targetX = distX * strength;
          targetY = distY * strength;
          if (!raf) raf = requestAnimationFrame(animate);
        }
      };

      const onMouseLeave = () => {
        isActive = false;
        targetX = 0;
        targetY = 0;
        if (!raf) raf = requestAnimationFrame(animate);
      };

      el.addEventListener("mousemove", onMouseMove, { passive: true });
      el.addEventListener("mouseleave", onMouseLeave);

      (el as any).__magnetic = { onMouseMove, onMouseLeave, raf };
    },

    unmounted(el: HTMLElement) {
      const data = (el as any).__magnetic;
      if (!data) return;
      el.removeEventListener("mousemove", data.onMouseMove);
      el.removeEventListener("mouseleave", data.onMouseLeave);
      if (data.raf) cancelAnimationFrame(data.raf);
      el.style.transform = "";
      delete (el as any).__magnetic;
    },
  });
});

import { usePreferredReducedMotion } from "@vueuse/core";
import { computed } from "vue";

/**
 * Composable for accessible animations that respect user motion preferences
 * Automatically disables animations when prefers-reduced-motion is enabled
 */
export function useAccessibleMotion() {
  // Check for reduced motion preference using VueUse core
  const reducedMotionPreference = usePreferredReducedMotion();
  const prefersReducedMotion = computed(() => reducedMotionPreference.value === "reduce");

  /**
   * Returns animation variant or static variant based on motion preference
   * @param animationVariant - The full animation variant to use
   * @param staticVariant - Optional static variant for reduced motion (defaults to visible state)
   */
  const variant = <T extends Record<string, any>>(
    animationVariant: T,
    staticVariant?: Partial<T>
  ): T => {
    if (prefersReducedMotion.value) {
      // Return static variant (no animation)
      return (staticVariant || animationVariant.visible || {}) as T;
    }
    return animationVariant;
  };

  return {
    prefersReducedMotion,
    variant,
    animationPresets,
  };
}

/**
 * Reusable animation presets matching the site's design system
 */
export const animationPresets = {
  // Entrance animations
  fadeInUp: {
    initial: { opacity: 0, y: 40 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 500, ease: "easeOut" },
    },
  },

  fadeIn: {
    initial: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { duration: 400, ease: "easeOut" },
    },
  },

  scaleIn: {
    initial: { opacity: 0, scale: 0.95 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: { duration: 400, ease: "easeOut" },
    },
  },

  slideInLeft: {
    initial: { opacity: 0, x: -40 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 500, ease: "easeOut" },
    },
  },

  slideInRight: {
    initial: { opacity: 0, x: 40 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 500, ease: "easeOut" },
    },
  },

  // Combined animations
  fadeInUpScale: {
    initial: { opacity: 0, y: 30, scale: 0.95 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { duration: 500, ease: "easeOut" },
    },
  },

  // Icon animations
  iconHover: {
    initial: { scale: 1, rotate: 0 },
    hover: {
      scale: 1.1,
      rotate: 5,
      transition: { type: "spring", stiffness: 300, damping: 15 },
    },
  },

  iconRotate: {
    initial: { rotate: 0 },
    hover: {
      rotate: 360,
      transition: { duration: 500, ease: "easeInOut" },
    },
  },

  // Button animations
  buttonLift: {
    initial: { y: 0 },
    hover: {
      y: -2,
      transition: { duration: 200, ease: "easeOut" },
    },
  },

  buttonGlow: {
    initial: { boxShadow: "0 0 0 0 rgba(204, 31, 53, 0)" },
    hover: {
      boxShadow: "0 0 20px 4px rgba(204, 31, 53, 0.3)",
      transition: { duration: 300, ease: "easeOut" },
    },
  },

  // Form validation animations
  shake: {
    initial: { x: 0 },
    error: {
      x: [0, -10, 10, -10, 10, 0],
      transition: { duration: 400, ease: "easeInOut" },
    },
  },

  successPulse: {
    initial: { scale: 1 },
    success: {
      scale: [1, 1.05, 1],
      transition: { duration: 500, ease: "easeOut" },
    },
  },

  // Stagger utilities
  staggerContainer: {
    initial: {},
    visible: {
      transition: {
        staggerChildren: 0.1,
      },
    },
  },

  staggerItem: {
    initial: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 400, ease: "easeOut" },
    },
  },
} as const;

/**
 * Helper to create staggered animations for lists
 * @param baseDelay - Base delay in milliseconds
 * @param itemDelay - Delay between items in milliseconds
 * @param index - Item index in the list
 */
export function getStaggerDelay(
  baseDelay: number = 0,
  itemDelay: number = 100,
  index: number = 0
): number {
  return baseDelay + index * itemDelay;
}

/**
 * Creates a custom animation variant with stagger delay
 * @param preset - Animation preset name
 * @param delay - Delay in milliseconds
 */
export function withDelay(
  preset: keyof typeof animationPresets,
  delay: number
): any {
  const basePreset = animationPresets[preset];
  return {
    ...basePreset,
    visible: {
      ...basePreset.visible,
      transition: {
        ...(basePreset.visible.transition || {}),
        delay: delay / 1000, // Convert to seconds for motion library
      },
    },
  };
}

// composables/useScrollAnimation.ts
import { ref, onMounted, onUnmounted } from 'vue'
import type { Ref } from 'vue'

interface ScrollAnimationOptions {
  threshold?: number
  rootMargin?: string
  once?: boolean // Only trigger animation once
}

/**
 * Basic scroll animation composable with IntersectionObserver
 * Adds 'visible' class to elements when they enter viewport
 */
export const useScrollAnimation = (options: ScrollAnimationOptions = {}) => {
  const {
    threshold = 0.1,
    rootMargin = '0px',
    once = true,
  } = options

  let observer: IntersectionObserver | null = null

  const observeElement = (element: Element) => {
    if (!element) return

    // Immediately observe the element
    observer?.observe(element)
  }

  const unobserveElement = (element: Element) => {
    if (!element) return
    observer?.unobserve(element)
  }

  onMounted(() => {
    // Create simple intersection observer
    observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            // Add 'visible' class when element enters viewport
            entry.target.classList.add('visible')

            // Optionally stop observing after first trigger
            if (once) {
              observer?.unobserve(entry.target)
            }
          } else if (!once) {
            // Remove class if observing continuously
            entry.target.classList.remove('visible')
          }
        })
      },
      { threshold, rootMargin }
    )
  })

  onUnmounted(() => {
    observer?.disconnect()
    observer = null
  })

  return {
    observeElement,
    unobserveElement,
  }
}

/**
 * Advanced scroll animation with ref-based visibility tracking
 * Returns reactive visibility state and target ref
 */
export function useScrollAnimationRef(
  threshold: number = 0.2,
  rootMargin: string = '0px',
  once: boolean = true
) {
  const isVisible = ref(false)
  const targetRef: Ref<HTMLElement | null> = ref(null)
  let observer: IntersectionObserver | null = null

  onMounted(() => {
    if (!targetRef.value) return

    observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            isVisible.value = true

            // Optionally stop observing after first trigger
            if (once && observer && targetRef.value) {
              observer.unobserve(targetRef.value)
            }
          } else if (!once) {
            isVisible.value = false
          }
        })
      },
      {
        threshold,
        rootMargin,
      }
    )

    observer.observe(targetRef.value)
  })

  onUnmounted(() => {
    if (observer && targetRef.value) {
      observer.unobserve(targetRef.value)
      observer.disconnect()
    }
  })

  return {
    targetRef,
    isVisible,
  }
}

/**
 * Staggered scroll animations for list items
 * Triggers animations sequentially with delay
 */
export function useStaggeredScrollAnimation(
  staggerDelay: number = 100,
  threshold: number = 0.2
) {
  const containerRef: Ref<HTMLElement | null> = ref(null)
  let observer: IntersectionObserver | null = null

  onMounted(() => {
    if (!containerRef.value) return

    observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const children = Array.from(entry.target.children)

            // Trigger staggered animations
            children.forEach((child, index) => {
              setTimeout(() => {
                child.classList.add('visible')
              }, index * staggerDelay)
            })

            // Stop observing after trigger
            if (observer && containerRef.value) {
              observer.unobserve(containerRef.value)
            }
          }
        })
      },
      {
        threshold,
      }
    )

    observer.observe(containerRef.value)
  })

  onUnmounted(() => {
    if (observer && containerRef.value) {
      observer.unobserve(containerRef.value)
      observer.disconnect()
    }
  })

  return {
    containerRef,
  }
}

/**
 * Parallax scroll effect composable
 * Returns reactive scroll progress (0-1) for element
 */
export function useParallaxScroll(speed: number = 0.5) {
  const targetRef: Ref<HTMLElement | null> = ref(null)
  const scrollProgress = ref(0)

  const updateParallax = () => {
    if (!targetRef.value) return

    const rect = targetRef.value.getBoundingClientRect()
    const viewportHeight = window.innerHeight

    // Calculate progress (0 when element enters bottom, 1 when it exits top)
    const progress = (viewportHeight - rect.top) / (viewportHeight + rect.height)
    scrollProgress.value = Math.max(0, Math.min(1, progress))

    // Apply transform
    const translateY = (scrollProgress.value - 0.5) * 100 * speed
    targetRef.value.style.transform = `translateY(${translateY}px)`
  }

  onMounted(() => {
    window.addEventListener('scroll', updateParallax, { passive: true })
    updateParallax() // Initial calculation
  })

  onUnmounted(() => {
    window.removeEventListener('scroll', updateParallax)
  })

  return {
    targetRef,
    scrollProgress,
  }
}

