 2→import { ref, onMounted, onUnmounted } from "vue";
 3→
 4→const { t, tm } = useI18n();
 5→
 6→// Scroll animation
 7→const sectionRef = ref<HTMLElement | null>(null);
 8→const isVisible = ref(false);
 9→
10→onMounted(() => {
11→  if (!sectionRef.value) return;
12→
13→  const observer = new IntersectionObserver(
14→    (entries) => {
15→      entries.forEach((entry) => {
16→        if (entry.isIntersecting) {
17→          isVisible.value = true;
18→          observer.disconnect();
19→        }
20→      });
21→    },
22→    { threshold: 0.15 },
23→  );
24→
25→  observer.observe(sectionRef.value);
26→
27→  onUnmounted(() => {
28→    observer.disconnect();
29→  });
30→});
