  1→<script setup lang="ts">
  2→import { ref, computed, onMounted, onUnmounted } from "vue";
  3→
  4→const { t, tm } = useI18n();
  5→
  6→const sectionRef = ref<HTMLElement | null>(null);
  7→const sliderRef = ref<HTMLElement | null>(null);
  8→const isVisible = ref(false);
  9→const sliderPosition = ref(50);
 10→const isDragging = ref(false);
 11→
 12→const metrics = computed(() =>
 13→  tm("services.beforeAfter.metrics") as Array<{
 14→    label: string;
 15→    before: string;
 16→    after: string;
 17→    unit: string;
 18→    improved: boolean;
 19→  }>
 20→);
 21→
 22→function updateSlider(clientX: number) {
 23→  if (!sliderRef.value) return;
 24→  const rect = sliderRef.value.getBoundingClientRect();
 25→  const x = clientX - rect.left;
 26→  const percent = Math.max(0, Math.min(100, (x / rect.width) * 100));
 27→  sliderPosition.value = percent;
 28→}
 29→
 30→function onPointerDown(e: PointerEvent) {
 31→  isDragging.value = true;
 32→  (e.target as HTMLElement).setPointerCapture(e.pointerId);
 33→  updateSlider(e.clientX);
 34→}
 35→
 36→function onPointerMove(e: PointerEvent) {
 37→  if (!isDragging.value) return;
 38→  updateSlider(e.clientX);
 39→}
 40→
 41→function onPointerUp() {
 42→  isDragging.value = false;
 43→}
 44→
 45→function onKeyDown(e: KeyboardEvent) {
 46→  if (e.key === "ArrowLeft") {
 47→    sliderPosition.value = Math.max(0, sliderPosition.value - 2);
 48→    e.preventDefault();
 49→  } else if (e.key === "ArrowRight") {
 50→    sliderPosition.value = Math.min(100, sliderPosition.value + 2);
 51→    e.preventDefault();
 52→  }
 53→}
 54→
 55→onMounted(() => {
 56→  if (!sectionRef.value) return;
 57→  const observer = new IntersectionObserver(
 58→    ([entry]) => {
 59→      if (entry.isIntersecting) {
 60→        isVisible.value = true;
 61→        observer.disconnect();
 62→      }
 63→    },
 64→    { threshold: 0.15 }
 65→  );
 66→  observer.observe(sectionRef.value);
 67→
 68→  onUnmounted(() => observer.disconnect());
 69→});
