 82→      entries.forEach((entry) => {
 83→        if (entry.isIntersecting) {
 84→          isVisible.value = true;
 85→          sectionObs.disconnect();
 86→        }
 87→      });
 88→    },
 89→    { threshold: 0.05 }
 90→  );
 91→  sectionObs.observe(sectionRef.value);
 92→
 93→  // Individual step stagger animation (one-time reveal)
 94→  stepObserver = new IntersectionObserver(
 95→    (entries) => {
 96→      entries.forEach((entry) => {
 97→        if (entry.isIntersecting) {
 98→          const key = (entry.target as HTMLElement).dataset.step;
 99→          if (key) visibleSteps.value.add(key);
100→          stepObserver?.unobserve(entry.target);
101→        }
102→      });
103→    },
104→    { threshold: 0.15, rootMargin: "0px 0px -50px 0px" }
105→  );
106→
107→  // Active step tracking (continuous - highlights current step)
108→  if (!prefersReduced) {
109→    activeObserver = new IntersectionObserver(
110→      (entries) => {
111→        entries.forEach((entry) => {
112→          const key = (entry.target as HTMLElement).dataset.step;
113→          if (!key) return;
114→          if (entry.isIntersecting) {
115→            activeStep.value = key;
116→          }
117→        });
118→      },
119→      { threshold: 0.4, rootMargin: "-20% 0px -40% 0px" }
120→    );
121→  }
122→
123→  const stepElements = sectionRef.value.querySelectorAll("[data-step]");
124→  stepElements.forEach((el) => {
125→    stepObserver!.observe(el);
126→    activeObserver?.observe(el);
127→  });
128→
129→  onUnmounted(() => {
130→    sectionObs.disconnect();
131→    stepObserver?.disconnect();
132→    activeObserver?.disconnect();
133→  });
134→});
