  6→const { t, tm } = useI18n();
  7→const { cursorX, cursorY, prefersReducedMotion } = useCursorFollow();
  8→
  9→const featuredRef = ref<HTMLElement | null>(null);
 10→const gridRef = ref<HTMLElement | null>(null);
 11→
 12→useStrokeDraw(gridRef, {
 13→  delay: 300,
 14→  stagger: 150,
 15→  duration: 700,
 16→  selector: ".value-icon-wrap",
