 23→// Cursor-following shine on featured card
 24→const shineStyle = computed(() => {
 25→  if (prefersReducedMotion.value || !featuredRef.value) return {};
 26→  const rect = featuredRef.value?.getBoundingClientRect();
 27→  if (!rect) return {};
 28→  const x = cursorX.value - rect.left;
 29→  const y = cursorY.value - rect.top;
 30→  return {
 31→    background: `radial-gradient(400px circle at ${x}px ${y}px, rgba(42, 147, 134, 0.08), transparent 60%)`,
 32→  };
 33→});
