  1→<script setup lang="ts">
  2→import { ref, onMounted, onUnmounted } from "vue";
  3→import { useStrokeDraw } from "~/composables/useStrokeDraw";
  4→
  5→const { t } = useI18n();
  6→
  7→// Scroll animation
  8→const sectionRef = ref<HTMLElement | null>(null);
  9→const gridRef = ref<HTMLElement | null>(null);
 10→
 11→useStrokeDraw(gridRef, {
 12→  delay: 200,
 13→  stagger: 150,
 14→  duration: 700,
 15→  selector: ".value-icon-wrapper",
 16→});
 17→const isVisible = ref(false);
 18→
 19→onMounted(() => {
 20→  if (!sectionRef.value) return;
 21→
 22→  const observer = new IntersectionObserver(
 23→    (entries) => {
 24→      entries.forEach((entry) => {
 25→        if (entry.isIntersecting) {
 26→          isVisible.value = true;
 27→          observer.disconnect();
 28→        }
 29→      });
 30→    },
 31→    { threshold: 0.2 }
 32→  );
 33→
 34→  observer.observe(sectionRef.value);
 35→
 36→  onUnmounted(() => {
 37→    observer.disconnect();
 38→  });
 39→});
 40→
 41→// Value cards data using i18n
 42→const values = [
