  1→<script setup lang="ts">
  2→import { ref, computed, onMounted, onUnmounted, nextTick } from "vue";
  3→
  4→const { t } = useI18n();
  5→
  6→const sectionRef = ref<HTMLElement | null>(null);
  7→const isVisible = ref(false);
  8→const isSubmitting = ref(false);
  9→const submitSuccess = ref(false);
 10→const submitError = ref(false);
 11→
 12→// Form data
 13→const formData = ref({
 14→  name: "",
 15→  email: "",
 16→  phone: "",
 17→  company: "",
 18→  interest: "",
 19→  subject: "",
 20→  message: "",
 21→});
 22→
 23→onMounted(() => {
 24→  if (!sectionRef.value) return;
 25→
 26→  const observer = new IntersectionObserver(
 27→    (entries) => {
 28→      entries.forEach((entry) => {
 29→        if (entry.isIntersecting) {
 30→          isVisible.value = true;
 31→          observer.disconnect();
 32→        }
 33→      });
 34→    },
 35→    { threshold: 0.1 }
 36→  );
 37→
 38→  observer.observe(sectionRef.value);
 39→
 40→  onUnmounted(() => {
 41→    observer.disconnect();
 42→  });
 43→});
 44→
 45→// Interest dropdown
 46→const interestOptions = computed(() => [
 47→  { value: "website", label: t("contact.form.interest.options.website") },
 48→  { value: "identity", label: t("contact.form.interest.options.identity") },
 49→  { value: "cms", label: t("contact.form.interest.options.cms") },
 50→  { value: "support", label: t("contact.form.interest.options.support") },
 51→  { value: "consulting", label: t("contact.form.interest.options.consulting") },
 52→  { value: "other", label: t("contact.form.interest.options.other") },
 53→]);
 54→
 55→const isDropdownOpen = ref(false);
 56→const dropdownRef = ref<HTMLElement | null>(null);
 57→const focusedOptionIndex = ref(-1);
 58→
 59→const selectedLabel = computed(() => {
 60→  const selected = interestOptions.value.find(
 61→    (opt) => opt.value === formData.value.interest
 62→  );
 63→  return selected?.label || "";
 64→});
 65→
 66→const toggleDropdown = () => {
 67→  isDropdownOpen.value = !isDropdownOpen.value;
 68→  if (isDropdownOpen.value) {
 69→    focusedOptionIndex.value = interestOptions.value.findIndex(
 70→      (opt) => opt.value === formData.value.interest
 71→    );
 72→  }
 73→};
 74→
 75→const selectOption = (value: string) => {
 76→  formData.value.interest = value;
 77→  isDropdownOpen.value = false;
 78→  // Return focus to trigger
 79→  nextTick(() => {
 80→    dropdownRef.value?.querySelector<HTMLElement>(".dropdown-trigger")?.focus();
 81→  });
 82→};
 83→
 84→const handleDropdownKeydown = (event: KeyboardEvent) => {
 85→  const options = interestOptions.value;
 86→
 87→  switch (event.key) {
 88→    case "Enter":
 89→    case " ":
 90→      event.preventDefault();
 91→      if (isDropdownOpen.value && focusedOptionIndex.value >= 0) {
 92→        selectOption(options[focusedOptionIndex.value].value);
 93→      } else {
 94→        toggleDropdown();
 95→      }
 96→      break;
 97→    case "ArrowDown":
 98→      event.preventDefault();
 99→      if (!isDropdownOpen.value) {
100→        isDropdownOpen.value = true;
101→        focusedOptionIndex.value = 0;
102→      } else {
103→        focusedOptionIndex.value = Math.min(
104→          focusedOptionIndex.value + 1,
105→          options.length - 1
106→        );
107→      }
108→      break;
109→    case "ArrowUp":
110→      event.preventDefault();
111→      if (isDropdownOpen.value) {
112→        focusedOptionIndex.value = Math.max(focusedOptionIndex.value - 1, 0);
113→      }
114→      break;
115→    case "Escape":
116→      event.preventDefault();
117→      isDropdownOpen.value = false;
118→      break;
119→    case "Home":
120→      if (isDropdownOpen.value) {
121→        event.preventDefault();
122→        focusedOptionIndex.value = 0;
123→      }
124→      break;
125→    case "End":
126→      if (isDropdownOpen.value) {
127→        event.preventDefault();
128→        focusedOptionIndex.value = options.length - 1;
129→      }
130→      break;
131→  }
132→};
133→
134→// Close dropdown on outside click
135→const handleClickOutside = (event: MouseEvent) => {
136→  if (
137→    dropdownRef.value &&
138→    !dropdownRef.value.contains(event.target as Node)
139→  ) {
140→    isDropdownOpen.value = false;
141→  }
142→};
143→
144→onMounted(() => {
145→  document.addEventListener("click", handleClickOutside);
146→});
147→
148→onUnmounted(() => {
149→  document.removeEventListener("click", handleClickOutside);
150→});
151→
152→// Form submission
153→const handleSubmit = async (event: Event) => {
154→  event.preventDefault();
155→  if (isSubmitting.value) return;
156→
157→  isSubmitting.value = true;
158→  submitSuccess.value = false;
159→  submitError.value = false;
160→
161→  try {
162→    const form = event.target as HTMLFormElement;
163→    const data = new FormData(form);
164→
165→    const response = await fetch(form.action, {
166→      method: "POST",
167→      body: data,
168→      headers: {
169→        Accept: "application/json",
170→      },
171→    });
172→
173→    if (response.ok) {
174→      submitSuccess.value = true;
175→      formData.value = {
176→        name: "",
177→        email: "",
178→        phone: "",
179→        company: "",
180→        interest: "",
181→        subject: "",
182→        message: "",
183→      };
184→    } else {
185→      submitError.value = true;
186→    }
187→  } catch {
188→    submitError.value = true;
189→  } finally {
190→    isSubmitting.value = false;
191→  }
192→};
193→</script>
